export type CardNameType =
  | 'AMEX'
  | 'DANKORT'
  | 'HIPERCARD'
  | 'DINERSCLUB'
  | 'DISCOVER'
  | 'JCB'
  | 'LASER'
  | 'MAESTRO'
  | 'MASTERCARD'
  | 'UNIONPAY'
  | 'VISAELECTRON'
